mapper.select:
select id, username, password, email, status
from user
where id = #{id}

select id, username, password, email, status
from user
where username = #{name}

select id, username, password, email, status
from user
where email = #{email}

mapper.update:
update user
set username = #{username},
    password = #{password},
    email    = #{email}
where id = #{id}
